def censor(message):
    profane_list = blacklist_word
    pattern = "|".join(r"\b{}\b".format(re.escape(word)) for word in profane_list)
    censored_message = re.sub(
        pattern,
        lambda match: "*" * len(match.group()),
        message,
        flags=re.IGNORECASE
    )
    return censored_message
